let count=0;const value=document.getElementById("value"),btns=document.querySelectorAll(".btn");btns.forEach(t=>{t.addEventListener("click",t=>{const e=t.currentTarget.classList;e.contains("decrease")?count--:e.contains("increase")?count++:count=0,value.style.color=count>0?"green":count<0?"red":"#222",value.textContent=count})});