const date=document.getElementById("date");date.innerHTML=(new Date).getFullYear();const navToggle=document.querySelector(".nav-toggle"),linksContainer=document.querySelector(".links-container"),links=document.querySelector(".links");navToggle.addEventListener("click",()=>{const e=linksContainer.getBoundingClientRect(),n=links.getBoundingClientRect().height;linksContainer.style.height=0===e?`${n}px`:0});const navbar=document.getElementById("nav"),topLink=document.querySelector(".top-link");window.addEventListener("scroll",()=>{const e=window.pageYOffset,n=navbar.getBoundingClientRect().height;e>n?navbar.classList.add("fixed-nav"):navbar.classList.remove("fixed-nav"),e>500?topLink.classList.add("show-link"):topLink.classList.remove("show-link")});const scrollLinks=document.querySelectorAll(".scroll-link");scrollLinks.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault();e.currentTarget.getAttribute("href")})});